//Crear un gr치fico con datos hard coded
//pasarle los datos desde un api
//incluir el gr치fico en el html
//ajustar el css para que se vea bien en una fila
//Instalar npm install react-chartjs-2
//https://www.youtube.com/watch?v=c_9c5zkfQ3Y

import React from 'react';
import { Bar } from 'react-chartjs-2';
import { defaults } from 'react-chartjs-2';

defaults.global.tooltips.enabled = false;
defaults.global.legend.position = 'bottom';

const BarChart = () => {
  return (
    <div>
      data=
      {{
        labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
        datasets: [
          {
            label: '# of votes',
            data: [12, 19, 3, 5, 2, 3],
            backgroundColor: [
              'rgba(255, 99, 132, 0.2)',
              'rgba(54, 162, 235, 0.2)',
              'rgba(255, 206, 86, 0.2)',
              'rgba(75, 192, 192, 0.2)',
              'rgba(153, 102, 255, 0.2)',
              'rgba(255, 159, 64, 0.2)',
            ],
            borderColor: [
              'rgba(255, 99, 132, 1)',
              'rgba(54, 162, 235, 1)',
              'rgba(255, 206, 86, 1)',
              'rgba(75, 192, 192, 1)',
              'rgba(153, 102, 255, 1)',
              'rgba(255, 159, 64, 1)',
            ],
            borderWidth: 1,
          },
          // {
          //   label: 'Quantity',
          //   data: [47, 52, 67, 58, 9, 50],
          //   backgroundColor: 'orange',
          //   borderColor: 'red',
          // },
        ],
      }}
      height={400}
      width={600}
      options=
      {{
        maintainAspectRatio: false,
        scales: {
          yAxes: [
            {
              ticks: {
                beginAtZero: true,
              },
            },
          ],
        },
        legend: {
          labels: {
            fontSize: 25,
          },
        },
      }}
      />
    </div>
  );
};

export default BarChart;
